function val = A_mm(m,l,k,k1,R1,R2,h1,h2,mode)
%The function is used to compute A_mm
%
%Input: 
%mode: 1 implies odd mode while 0 implies even mode.
%Precondition:
%Output:
%Postcondition:

a=R1*h1/(R2*h2);
if(m==0)
    f=@(s1,s2,theta)exp(1i.*k.*sqrt((R2-R1+R2.*h2.*s2).^2+4.*R1.*R2.*(1+h1.*s1).*(1+h2.*(s2+a.*s1)).*sin(theta/2).^2))./(4.*pi.*sqrt((R2-R1+R2.*h2.*s2).^2+4.*R1.*R2.*(1+h1.*s1).*(1+h2.*(s2+a.*s1)).*sin(theta/2).^2)).*cos(theta);
    %f=@(s1,s2,theta)(exp(1i.*k.*sqrt((R2-R1+R2.*h2.*s2).^2+4.*R1.*R2.*(1+h1.*s1).*(1+h2.*(s2+a.*s1)).*sin(theta/2).^2))-1)./(sqrt((R2-R1+R2.*h2.*s2).^2+4.*R1.*R2.*(1+h1.*s1).*(1+h2.*(s2+a.*s1)).*sin(theta/2).^2)).*cos(theta);
    c1=@(x)-a*x;
    c2=@(x)1-a*x;
    val=-cos(k*l/2+pi/2*mode)/(pi*k*log(1+h1))*2*pi*k.^2*(R1*h1*R2*h2)*...
        2.*integral3(f,0,1,c1,c2,0,pi,"AbsTol",0,"RelTol",1e-8);
else
    m1=m;
    m=abs(m);
    beta1=accu_beta_N(m,0,R1,h1);
    beta2=accu_beta_N(m,0,R2,h2);
    lambda1=eigenvalue_N(m,0,R1,h1);
    lambda2=eigenvalue_N(m,0,R2,h2);
    s1=s_mn_N(k1,m,0,R1,h1);
    s2=s_mn_N(k1,m,0,R2,h2);
    %f1=@(s1,s2,theta)exp(1i.*k.*sqrt(h.^2.*(s2).^2+4.*(1+s1.*h).*(1+(s1+s2).*h).*sin(theta./2).^2))./(4.*pi.*sqrt(h.^2.*s2.^2+4.*(1+s1.*h).*(1+(s2+s1).*h).*sin(theta./2).^2)).*...
    %    ((bessely(m-1,beta)-bessely(m+1,beta))./2.*besselj(m,beta.*(1+s1.*h))-(besselj(m-1,beta)-besselj(m+1,beta))./2.*bessely(m,beta.*(1+s1.*h))).*...
    %    ((bessely(m-1,beta)-bessely(m+1,beta))./2.*besselj(m,beta.*(1+(s2+s1).*h))-(besselj(m-1,beta)-besselj(m+1,beta))./2.*bessely(m,beta.*(1+(s2+s1).*h))).*...
    %    cos(m1.*theta).*(1+s1.*h).*(1+(s1+s2).*h);
    %f2=@(s1,s2,theta)exp(1i.*k.*sqrt(h.^2.*(s2).^2+4.*(1+s1.*h).*(1+(s1+s2).*h).*sin(theta./2).^2))./(4.*pi.*sqrt(h.^2.*s2.^2+4.*(1+s1.*h).*(1+(s2+s1).*h).*sin(theta./2).^2)).*...
    %    ((bessely(m-1,beta)-bessely(m+1,beta))./2.*(besselj(m-1,beta.*(1+s1.*h))-besselj(m+1,beta.*(1+s1.*h)))./2-(besselj(m-1,beta)-besselj(m+1,beta))./2.*(bessely(m-1,beta.*(1+s1.*h))-bessely(m+1,beta.*(1+s1.*h)))./2).*...
    %    ((bessely(m-1,beta)-bessely(m+1,beta))./2.*(besselj(m-1,beta.*(1+(s2+s1).*h))-besselj(m+1,beta.*(1+(s2+s1).*h)))./2-(besselj(m-1,beta)-besselj(m+1,beta))./2.*(bessely(m-1,beta.*(1+(s2+s1).*h))-bessely(m+1,beta.*(1+(s2+s1).*h)))./2).*...
    %    cos(m1.*theta).*cos(theta).*(1+s1.*h).*(1+(s1+s2).*h);
    %f3=@(s1,s2,theta)exp(1i.*k.*sqrt(h.^2.*(s2).^2+4.*(1+s1.*h).*(1+(s1+s2).*h).*sin(theta./2).^2))./(4.*pi.*sqrt(h.^2.*s2.^2+4.*(1+s1.*h).*(1+(s2+s1).*h).*sin(theta./2).^2)).*...
    %    ((bessely(m-1,beta)-bessely(m+1,beta))./2.*(besselj(m-1,beta.*(1+s1.*h))-besselj(m+1,beta.*(1+s1.*h)))./2-(besselj(m-1,beta)-besselj(m+1,beta))./2.*(bessely(m-1,beta.*(1+s1.*h))-bessely(m+1,beta.*(1+s1.*h)))./2).*...
    %    ((bessely(m-1,beta)-bessely(m+1,beta))./2.*besselj(m,beta.*(1+(s2+s1).*h))-(besselj(m-1,beta)-besselj(m+1,beta))./2.*bessely(m,beta.*(1+(s2+s1).*h))).*...
    %    -1i.*sin(m1.*theta).*sin(theta).*(1+s1.*h);
    %f4=@(s1,s2,theta)exp(1i.*k.*sqrt(h.^2.*(s2).^2+4.*(1+s1.*h).*(1+(s1+s2).*h).*sin(theta./2).^2))./(4.*pi.*sqrt(h.^2.*s2.^2+4.*(1+s1.*h).*(1+(s2+s1).*h).*sin(theta./2).^2)).*...
    %    ((bessely(m-1,beta)-bessely(m+1,beta))./2.*besselj(m,beta.*(1+s1.*h))-(besselj(m-1,beta)-besselj(m+1,beta))./2.*bessely(m,beta.*(1+s1.*h))).*...
    %    ((bessely(m-1,beta)-bessely(m+1,beta))./2.*(besselj(m-1,beta.*(1+(s2+s1).*h))-besselj(m+1,beta.*(1+(s2+s1).*h)))./2-(besselj(m-1,beta)-besselj(m+1,beta))./2.*(bessely(m-1,beta.*(1+(s2+s1).*h))-bessely(m+1,beta.*(1+(s2+s1).*h)))./2).*...
    %    -1i.*sin(m1.*theta).*sin(theta).*(1+(s1+s2).*h);
    %f5=@(s1,s2,theta)exp(1i.*k.*sqrt(h.^2.*(s2).^2+4.*(1+s1.*h).*(1+(s1+s2).*h).*sin(theta./2).^2))./(4.*pi.*sqrt(h.^2.*s2.^2+4.*(1+s1.*h).*(1+(s2+s1).*h).*sin(theta./2).^2)).*...
    %    ((bessely(m-1,beta)-bessely(m+1,beta))./2.*besselj(m,beta.*(1+s1.*h))-(besselj(m-1,beta)-besselj(m+1,beta))./2.*bessely(m,beta.*(1+s1.*h))).*...
    %    ((bessely(m-1,beta)-bessely(m+1,beta))./2.*besselj(m,beta.*(1+(s2+s1).*h))-(besselj(m-1,beta)-besselj(m+1,beta))./2.*bessely(m,beta.*(1+(s2+s1).*h))).*...
    %    cos(m1.*theta).*cos(theta);
    f1=@(s1,s2,theta)exp(1i.*k.*sqrt((R2-R1+R2.*h2.*s2).^2+4.*R1.*R2.*(1+h1.*s1).*(1+h2.*(s2+a.*s1)).*sin(theta/2).^2))./(4.*pi.*sqrt((R2-R1+R2.*h2.*s2).^2+4.*R1.*R2.*(1+h1.*s1).*(1+h2.*(s2+a.*s1)).*sin(theta/2).^2)).*...
        ((bessely(m-1,R1*beta1)-bessely(m+1,R1*beta1))./2.*besselj(m,beta1.*R1.*(1+s1.*h1))-(besselj(m-1,R1*beta1)-besselj(m+1,R1*beta1))./2.*bessely(m,beta1.*R1.*(1+s1.*h1))).*...
        ((bessely(m-1,R2*beta2)-bessely(m+1,R2*beta2))./2.*besselj(m,beta2.*R2.*(1+(s2+a*s1).*h2))-(besselj(m-1,R2*beta2)-besselj(m+1,R2*beta2))./2.*bessely(m,beta2.*R2.*(1+(s2+a*s1).*h2))).*...
        cos(m1.*theta).*R1.*(1+s1.*h1).*R2.*(1+(a*s1+s2).*h2);
    f2=@(s1,s2,theta)exp(1i.*k.*sqrt((R2-R1+R2.*h2.*s2).^2+4.*R1.*R2.*(1+h1.*s1).*(1+h2.*(s2+a.*s1)).*sin(theta/2).^2))./(4.*pi.*sqrt((R2-R1+R2.*h2.*s2).^2+4.*R1.*R2.*(1+h1.*s1).*(1+h2.*(s2+a.*s1)).*sin(theta/2).^2)).*...
        ((bessely(m-1,R1*beta1)-bessely(m+1,R1*beta1))./2.*(besselj(m-1,R1*beta1.*(1+s1.*h1))-besselj(m+1,R1*beta1.*(1+s1.*h1)))./2-(besselj(m-1,R1*beta1)-besselj(m+1,R1*beta1))./2.*(bessely(m-1,R1*beta1.*(1+s1.*h1))-bessely(m+1,R1*beta1.*(1+s1.*h1)))./2).*...
        ((bessely(m-1,R2*beta2)-bessely(m+1,R2*beta2))./2.*(besselj(m-1,R2*beta2.*(1+(s2+a.*s1).*h2))-besselj(m+1,R2*beta2.*(1+(s2+a.*s1).*h2)))./2-(besselj(m-1,R2*beta2)-besselj(m+1,R2*beta2))./2.*(bessely(m-1,R2*beta2.*(1+(s2+a.*s1).*h2))-bessely(m+1,R2*beta2.*(1+(s2+a.*s1).*h2)))./2).*...
        cos(m1.*theta).*cos(theta).*R1.*(1+s1.*h1).*R2.*(1+(a*s1+s2).*h2);
    f3=@(s1,s2,theta)exp(1i.*k.*sqrt((R2-R1+R2.*h2.*s2).^2+4.*R1.*R2.*(1+h1.*s1).*(1+h2.*(s2+a.*s1)).*sin(theta/2).^2))./(4.*pi.*sqrt((R2-R1+R2.*h2.*s2).^2+4.*R1.*R2.*(1+h1.*s1).*(1+h2.*(s2+a.*s1)).*sin(theta/2).^2)).*...
        ((bessely(m-1,R1.*beta1)-bessely(m+1,R1.*beta1))./2.*(besselj(m-1,beta1.*R1.*(1+s1.*h1))-besselj(m+1,beta1.*R1.*(1+s1.*h1)))./2-(besselj(m-1,R1.*beta1)-besselj(m+1,R1.*beta1))./2.*(bessely(m-1,beta1.*R1.*(1+s1.*h1))-bessely(m+1,beta1.*R1.*(1+s1.*h1)))./2).*...
        ((bessely(m-1,R2.*beta2)-bessely(m+1,R2.*beta2))./2.*besselj(m,beta2.*R2.*(1+(s2+a*s1).*h2))-(besselj(m-1,R2.*beta2)-besselj(m+1,R2.*beta2))./2.*bessely(m,beta2.*R2.*(1+(s2+a*s1).*h2))).*...
        -1i.*sin(m1.*theta).*sin(theta).*R1.*(1+s1.*h1);
    f4=@(s1,s2,theta)exp(1i.*k.*sqrt((R2-R1+R2.*h2.*s2).^2+4.*R1.*R2.*(1+h1.*s1).*(1+h2.*(s2+a.*s1)).*sin(theta/2).^2))./(4.*pi.*sqrt((R2-R1+R2.*h2.*s2).^2+4.*R1.*R2.*(1+h1.*s1).*(1+h2.*(s2+a.*s1)).*sin(theta/2).^2)).*...
        ((bessely(m-1,R1.*beta1)-bessely(m+1,R1.*beta1))./2.*besselj(m,beta1.*R1.*(1+s1.*h1))-(besselj(m-1,R1.*beta1)-besselj(m+1,R1.*beta1))./2.*bessely(m,beta1.*R1.*(1+s1.*h1))).*...
        ((bessely(m-1,R2.*beta2)-bessely(m+1,R2.*beta2))./2.*(besselj(m-1,beta2.*R2.*(1+(s2+a*s1).*h2))-besselj(m+1,beta2.*R2.*(1+(s2+a*s1).*h2)))./2-(besselj(m-1,R2.*beta2)-besselj(m+1,R2.*beta2))./2.*(bessely(m-1,beta2.*R2.*(1+(s2+a*s1).*h2))-bessely(m+1,beta2.*R2.*(1+(s2+a*s1).*h2)))./2).*...
        -1i.*sin(m1.*theta).*sin(theta).*R2.*(1+(a*s1+s2).*h2);
    f5=@(s1,s2,theta)exp(1i.*k.*sqrt((R2-R1+R2.*h2.*s2).^2+4.*R1.*R2.*(1+h1.*s1).*(1+h2.*(s2+a.*s1)).*sin(theta/2).^2))./(4.*pi.*sqrt((R2-R1+R2.*h2.*s2).^2+4.*R1.*R2.*(1+h1.*s1).*(1+h2.*(s2+a.*s1)).*sin(theta/2).^2)).*...
        ((bessely(m-1,R1.*beta1)-bessely(m+1,R1.*beta1))./2.*besselj(m,beta1.*R1.*(1+s1.*h1))-(besselj(m-1,R1.*beta1)-besselj(m+1,R1.*beta1))./2.*bessely(m,beta1.*R1.*(1+s1.*h1))).*...
        ((bessely(m-1,R2.*beta2)-bessely(m+1,R2.*beta2))./2.*besselj(m,beta2.*R2.*(1+(s2+a*s1).*h2))-(besselj(m-1,R2.*beta2)-besselj(m+1,R2.*beta2))./2.*bessely(m,beta2.*R2.*(1+(s2+a*s1).*h2))).*...
        cos(m1.*theta).*cos(theta);
    g1=@(r)((bessely(m-1,R1.*beta1)-bessely(m+1,R1.*beta1))./2.*besselj(m,beta1.*r)-(besselj(m-1,R1.*beta1)-besselj(m+1,R1.*beta1))./2.*bessely(m,beta1.*r)).^2.*r;
    g2=@(r)((bessely(m-1,R2.*beta2)-bessely(m+1,R2.*beta2))./2.*besselj(m,beta2.*r)-(besselj(m-1,R2.*beta2)-besselj(m+1,R2.*beta2))./2.*bessely(m,beta2.*r)).^2.*r;
    C1=sqrt(2*pi*integral(g1,R1,R1*(1+h1),'AbsTol',1e-8,'RelTol',1e-8));
    C2=sqrt(2*pi*integral(g2,R2,R2*(1+h2),'AbsTol',1e-8,'RelTol',1e-8));
    c1=@(x)-a*x;
    c2=@(x)1-a*x;
    I1=integral3(f1,0,1,c1,c2,0,pi);
    I2=integral3(f2,0,1,c1,c2,0,pi);
    I3=integral3(f3,0,1,c1,c2,0,pi);
    I4=integral3(f4,0,1,c1,c2,0,pi);
    I5=integral3(f5,0,1,c1,c2,0,pi);
    val=-2*cos(s2*l/2+pi/2*mode)/(lambda1)*C1^(-1)*C2^(-1).*...
        2.*pi.*2.*(R1*h1*R2*h2).*(-lambda1.*lambda2.*I1+k.^2.*beta1.*beta2.*I2-k.^2.*beta1.*(-1i.*m1).*I3+k.^2.*beta2.*(1i.*m1).*I4+k.^2.*m1.^2.*I5);
end
end

